import React, { useState } from "react";
import Header from "./components/Header";
import CurrentLocation from "./components/CurrentLocation";
import "./App.css";


function getWeatherIcon(wmoCode) {
  const icons = new Map([
    [[0], "â˜€ï¸"],
    [[1], "ðŸŒ¤"],
    [[2], "â›…ï¸"],
    [[3], "â˜ï¸"],
    [[45, 48], "ðŸŒ«"],
    [[51, 56, 61, 66, 80], "ðŸŒ¦"],
    [[53, 55, 63, 65, 57, 67, 81, 82], "ðŸŒ§"],
    [[71, 73, 75, 77, 85, 86], "ðŸŒ¨"],
    [[95], "ðŸŒ©"],
    [[96, 99], "â›ˆ"],
  ]);

  const arr = [...icons.keys()].find((key) => key.includes(wmoCode));
  if (!arr) return "NOT FOUND";
  return icons.get(arr);
}

function convertToFlag(countryCode) {
  const codePoints = countryCode
    .toUpperCase()
    .split("")
    .map((char) => 127397 + char.charCodeAt());

  return String.fromCodePoint(...codePoints);
}

function formatDay(dateStr) {
  return new Intl.DateTimeFormat("en", {
    weekday: "short",
  }).format(new Date(dateStr));
}

function App() {
  const [location, setLocation] = useState("lisbon");
  const [isLoading, setIsLoading] = useState(false);
  const [displayLocation, setDisplayLocation] = useState("");
  const [weather, setWeather] = useState({});
  const [hourlyWeather, setHourlyWeather] = useState([]);

  async function fetchWeather() {
    try {
      setIsLoading(true);

      const geoRes = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${location}`
      );
      const geoData = await geoRes.json();
      console.log(geoData);

      if (!geoData.results) throw new Error("Location not found");

      const { latitude, longitude, timezone, name, country_code } =
        geoData.results[0];

      setDisplayLocation(`${name} ${convertToFlag(country_code)}`);

      const weatherRes = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&timezone=${timezone}&daily=weathercode,temperature_2m_max,temperature_2m_min&hourly=temperature_2m,weathercode`
      );
      const weatherData = await weatherRes.json();

      setWeather(weatherData.daily);
      setHourlyWeather(weatherData.hourly);
    } catch (err) {
      console.error(err);
    } finally {
      setIsLoading(false);
    }
  }

  return (
    <div className="app">
      <Header location={location} setLocation={setLocation} fetchWeather={fetchWeather} />
      <CurrentLocation />
      <div className="search_city">
        <input
          type="text"
          placeholder="Search for location..."
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        />
        <button onClick={fetchWeather}>Get weather</button>
        {isLoading && <p className="loader">Loading...</p>}
      </div>
      {weather.weathercode && (
        <Weather weather={weather} hourlyWeather={hourlyWeather} location={displayLocation} />
      )}
    </div>
  );
}

export default App;

function Weather({ weather, hourlyWeather, location }) {
  const {
    temperature_2m_max: max,
    temperature_2m_min: min,
    time: dates,
    weathercode: codes,
  } = weather;

  return (
    <div>
      <h2>Weather {location}</h2>
      <ul className="weather">
        {dates.map((date, i) => (
          <Day
            key={date}
            date={date}
            max={max[i]}
            min={min[i]}
            code={codes[i]}
            isToday={i === 0}
          />
        ))}
      </ul>
      <HourlyWeather hourlyWeather={hourlyWeather} />

    </div>
  );
}

function Day({ date, max, min, code, isToday }) {
  return (
    <li className="day">
      <span>{getWeatherIcon(code)}</span>
      <p>{isToday ? "Today" : formatDay(date)}</p>
      <p>
        {Math.floor(min)}&deg; &mdash; <strong>{Math.ceil(max)}&deg;</strong>
      </p>
    </li>
  );
}

function HourlyWeather({ hourlyWeather }) {
  return (
    <div className="hourly-weather">
      <h3>Hourly Weather</h3>
      <ul>
        {hourlyWeather.temperature_2m.map((temp, i) => (
          <li key={i}>
            <p>{formatHour(hourlyWeather.time[i])}</p>
            <p>{temp}Â°</p>
            <span>{getWeatherIcon(hourlyWeather.weathercode[i])}</span>
          </li>
        ))}
      </ul>
    </div>
  );
}

function formatHour(dateStr) {
  return new Intl.DateTimeFormat("en", {
    hour: "2-digit",
    minute: "2-digit",
  }).format(new Date(dateStr));
}
