import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Function to get user's location and fetch weather data
    const getLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          async (position) => {
            const { latitude, longitude } = position.coords;
            try {
              const response = await fetch(
                `https://api.meteoapi.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&apikey=6a4306a0f2c73c28dc5c3bbf739e87d0`
              );
              if (!response.ok) {
                throw new Error('Network response was not ok');
              }
              const data = await response.json();
              setWeather(data);
              setLoading(false);
            } catch (err) {
              setError('Failed to fetch weather data');
              setLoading(false);
            }
          },
          () => {
            setError('Geolocation permission denied');
            setLoading(false);
          }
        );
      } else {
        setError('Geolocation is not supported by this browser');
        setLoading(false);
      }
    };

    getLocation();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="App">
      <header className="App-header">
        <h1>Get Weather Update Here</h1>
      </header>
      <main>
        {weather && (
          <div>
            <h2>Current Weather</h2>
            <p>Location: {weather.location.name}</p>
            <p>Temperature: {weather.current.temperature}Â°C</p>
            <p>Weather: {weather.current.weather_descriptions[0]}</p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
