import React, { useState } from "react"; // Import React and the useState hook

// Function to get the weather icon based on WMO code
function getWeatherIcon(wmoCode) {
  // Create a map of weather icons associated with WMO codes
  const icons = new Map([
    [[0], "â˜€ï¸"], // Clear sky
    [[1], "ðŸŒ¤"], // Mainly clear
    [[2], "â›…ï¸"], // Partly cloudy
    [[3], "â˜ï¸"], // Overcast
    [[45, 48], "ðŸŒ«"], // Fog
    [[51, 56, 61, 66, 80], "ðŸŒ¦"], // Light rain showers
    [[53, 55, 63, 65, 57, 67, 81, 82], "ðŸŒ§"], // Moderate rain
    [[71, 73, 75, 77, 85, 86], "ðŸŒ¨"], // Snow
    [[95], "ðŸŒ©"], // Thunderstorm
    [[96, 99], "â›ˆ"], // Thunderstorm with hail
  ]);

  // Find the corresponding icon for the provided WMO code
  const arr = [...icons.keys()].find((key) => key.includes(wmoCode));
  if (!arr) return "NOT FOUND"; // Return "NOT FOUND" if no icon is found
  return icons.get(arr); // Return the found icon
}

// Function to convert a country code to its corresponding flag emoji
function convertToFlag(countryCode) {
  // Convert each character of the country code to its corresponding Unicode code point
  const codePoints = countryCode
    .toUpperCase() // Convert the country code to uppercase
    .split("") // Split the country code into individual characters
    .map((char) => 127397 + char.charCodeAt()); // Map each character to its flag emoji code point

  // Return the flag emoji string
  return String.fromCodePoint(...codePoints);
}

// Function to format a date string into the day of the week (e.g., "Mon", "Tue")
function formatDay(dateStr) {
  // Use Intl.DateTimeFormat to format the date string into a short weekday name
  return new Intl.DateTimeFormat("en", {
    weekday: "short", // Use short format (Mon, Tue, etc.)
  }).format(new Date(dateStr)); // Create a new Date object from the date string
}

// Main App component
function App() {
  // useState hook to manage the state of the location, loading status, display location, and weather data
  const [location, setLocation] = useState("lisbon"); // Default location is "lisbon"
  const [isLoading, setIsLoading] = useState(false); // Loading status, default is false
  const [displayLocation, setDisplayLocation] = useState(""); // Display location, initially empty
  const [weather, setWeather] = useState({}); // Weather data, initially an empty object

  // Function to fetch weather data based on the location
  async function fetchWeather() {
    try {
      setIsLoading(true); // Set loading status to true while fetching data

      // 1) Fetch the location (geocoding) data
      const geoRes = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${location}` // API call to get latitude, longitude, and other info
      );
      const geoData = await geoRes.json(); // Parse the JSON response
      console.log(geoData); // Log the geocoding data for debugging

      if (!geoData.results) throw new Error("Location not found"); // Throw an error if no location is found

      // Destructure the relevant location data from the response
      const { latitude, longitude, timezone, name, country_code } =
        geoData.results.at(0); // Use the first result

      // Set the display location with the name and flag emoji
      setDisplayLocation(`${name} ${convertToFlag(country_code)}`);

      // 2) Fetch the actual weather data based on latitude and longitude
      const weatherRes = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&timezone=${timezone}&daily=weathercode,temperature_2m_max,temperature_2m_min` // API call for weather data
      );
      const weatherData = await weatherRes.json(); // Parse the JSON response

      setWeather(weatherData.daily); // Set the weather data state with the daily forecast
    } catch (err) {
      console.error(err); // Log any errors to the console
    } finally {
      setIsLoading(false); // Set loading status to false after fetching is complete
    }
  }

  return (
    <div className="app">
      <h1>Classy Weather</h1> {/* Title of the app */}
      <div>
        <input
          type="text" // Input field for location search
          placeholder="Search for location..." // Placeholder text in the input field
          value={location} // The input value is tied to the location state
          onChange={(e) => setLocation(e.target.value)} // Update location state when the input changes
        />
      </div>
      <button onClick={fetchWeather}>Get weather</button> {/* Button to trigger fetching weather data */}

      {isLoading && <p className="loader">Loading...</p>} {/* Display loading text if data is being fetched */}

      {weather.weathercode && (
        <Weather weather={weather} location={displayLocation} /> // Display weather if data is available
      )}
    </div>
  );
}

export default App; // Export the App component as the default export

// Weather component to display the weather information
function Weather({ weather, location }) {
  // Destructure weather data properties for easier access
  const {
    temperature_2m_max: max, // Maximum temperatures
    temperature_2m_min: min, // Minimum temperatures
    time: dates, // Dates
    weathercode: codes, // Weather codes
  } = weather;

  return (
    <div>
      <h2>Weather {location}</h2> {/* Title showing the location */}
      <ul className="weather">
        {/* Map through each date to create a Day component */}
        {dates.map((date, i) => (
          <Day
            date={date} // Pass the date to the Day component
            max={max.at(i)} // Pass the max temperature for the day
            min={min.at(i)} // Pass the min temperature for the day
            code={codes.at(i)} // Pass the weather code for the day
            key={date} // Set a unique key for each list item
            isToday={i === 0} // Highlight the first item as "Today"
          />
        ))}
      </ul>
    </div>
  );
}

// Day component to display each day's weather information
function Day({ date, max, min, code, isToday }) {
  return (
    <li className="day">
      <span>{getWeatherIcon(code)}</span> {/* Display weather icon based on the code */}
      <p>{isToday ? "Today" : formatDay(date)}</p> {/* Display "Today" or the day of the week */}
      <p>
        {Math.floor(min)}&deg; &mdash; <strong>{Math.ceil(max)}&deg;</strong> {/* Display temperature range */}
      </p>
    </li>
  );
}
