import { useEffect, useState } from "react";




export default function Header() {
  const[value,setValue]=useState();


  const appid = "6a4306a0f2c73c28dc5c3bbf739e87d0";
  const lat = 44.34;
  const lon = 10.99;
  
  fetch(`https://api.openweathermap.org/data/3.0/onecall?lat=${lat}&lon=${lon}&exclude=hourly,minutely&appid=${appid}`)
    .then(response => response.json())
    .then(data => {
      console.log("7-Day Daily Forecast:", data.daily);
    });
  

  return (
    <div className='First-div'>
        <div><h1>WEATHER</h1></div>

        <div>
        <ul>
        <form>
           <input
            placeholder='search your location'
            value={value}
            onChange={(e)=>setValue(e.target.value)}
            
        ></input>
      </form>

       </ul>
        </div>
       
    </div>
  );
}
