import React, { useState, useEffect } from 'react';

const WeatherForecast = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const API_KEY = '6a4306a0f2c73c28dc5c3bbf739e87d0'; // Your API key
  const LAT = '44.34'; // Latitude for the desired location
  const LON = '10.99'; // Longitude for the desired location
  const DAYS = 7; // Number of days

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const response = await fetch(
          `https://api.openweathermap.org/data/2.5/forecast/daily?lat=${LAT}&lon=${LON}&cnt=${DAYS}&appid=${API_KEY}&units=metric`
        );

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setWeatherData(data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching weather data:', err);
        setError(err);
        setLoading(false);
      }
    };

    fetchWeatherData();
  }, [LAT, LON, API_KEY]);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error fetching weather data!</p>;

  return (
    <div>
      <h1>7-Day Weather Forecast</h1>
      {weatherData && weatherData.list.map((day, index) => (
        <div key={index}>
          <h2>{new Date(day.dt * 1000).toLocaleDateString()}</h2>
          <p>Temperature: {day.temp.day}Â°C</p>
          <p>Weather: {day.weather[0].description}</p>
          <p>Humidity: {day.humidity}%</p>
          <p>Wind Speed: {day.speed} m/s</p>
        </div>
      ))}
    </div>
  );
};

export default WeatherForecast;
