import './App.css';
import Header from './components/Header';
import CurrentLocation from './components/CurrentLocation';
import { useEffect } from 'react';




function Main(wmoCode)
{
//wmoCode it use a code to tract an application ..so the code has a code
//here i am passing wmoCODE

//first we deal with the currnt location situation
//alrigth i am going to use WMO(weather interpretation code)
//niw i must trnalste the wmo to what the icons will be acccesed
const weatherIcons = [
  { code: [0], icon: "‚òÄÔ∏è" },           // Clear sky
  { code: [1], icon: "üå§" },            // Mainly clear
  { code: [2], icon: "‚õÖÔ∏è" },           // Partly cloudy
  { code: [3], icon: "‚òÅÔ∏è" },           // Overcast
