function App() {
  // useState hook to manage the state of the location, loading status, display location, and weather data
  const [location, setLocation] = useState("lisbon"); // Default location is "lisbon"
  const [isLoading, setIsLoading] = useState(false); // Loading status, default is false
  const [displayLocation, setDisplayLocation] = useState(""); // Display location, initially empty
  const [weather, setWeather] = useState({}); // Weather data, initially an empty object

  // Function to fetch weather data based on the location
  async function fetchWeather() {
    try {
      setIsLoading(true); // Set loading status to true while fetching data

      // 1) Fetch the location (geocoding) data
      const geoRes = await fetch(
        `https://geocoding-api.open-meteo.com/v1/search?name=${location}` // API call to get latitude, longitude, and other info
      );
      const geoData = await geoRes.json(); // Parse the JSON response
      console.log(geoData); // Log the geocoding data for debugging

      if (!geoData.results) throw new Error("Location not found"); // Throw an error if no location is found

      // Destructure the relevant location data from the response
      const { latitude, longitude, timezone, name, country_code } =
        geoData.results.at(0); // Use the first result

      // Set the display location with the name and flag emoji
      setDisplayLocation(`${name} ${convertToFlag(country_code)}`);

      // 2) Fetch the actual weather data based on latitude and longitude
      const weatherRes = await fetch(
        `https://api.open-meteo.com/v1/forecast?latitude=${latitude}&longitude=${longitude}&timezone=${timezone}&daily=weathercode,temperature_2m_max,temperature_2m_min` // API call for weather data
      );
      const weatherData = await weatherRes.json(); // Parse the JSON response

      setWeather(weatherData.daily); // Set the weather data state with the daily forecast
    } catch (err) {
      console.error(err); // Log any errors to the console
    } finally {
      setIsLoading(false); // Set loading status to false after fetching is complete
    }
  }
  return (
    <div className="App">
      <Header name={name} setName={setName}/>
      <h1>Get weather update here </h1>
      <CurrentLocation/>

    </div>
  );
}

export default App;
