import React, { useEffect, useState } from 'react';

export default function CurrentLocation() {
    //taking control of the Location
    const [location, setLocation] = useState(null);

    useEffect(() => {
        async function fetchCurrentLocation() {
            if (navigator.geolocation.getCurrentPosition) {
                navigator.geolocation.getCurrentPosition(async (position) => {
                    const lat = position.coords.latitude;
                    const lon = position.coords.longitude;
                    const appid = "6a4306a0f2c73c28dc5c3bbf739e87d0";

                    try {
                        const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${appid}`);
                        const data = await response.json();
                        console.log(data);
                        //taking control of the data that is fetched using the array
                        setLocation(data);
                        // Save location data to sessionStorage
                        StoreLocation(lat, lon);
                    } catch (error) {
                        console.error('Error fetching the location data:', error);
                    }
                }, () => {
                    alert('No location detected ðŸ˜”');
                });
            } else {
                alert('Geolocation is not supported by this browser.');
            }
        }
        fetchCurrentLocation();
    }, []);

    // Save data into sessionStorage
    function StoreLocation(lat, lon) {
        const LocationCurrent = { lat, lon };
        localStorage.setItem('MyLocation', JSON.stringify(LocationCurrent));
    }
    
        const date=new Date();
        console.log(date);
    

        return (
            <div>
                {location ? (
                    <>
                        <h1>Your Current Location: {location.name}</h1>
    
                        <ul className='Current'>
                            <h3>Today's Weather</h3>
                            <img src="https://openweathermap.org/img/wn/10d@2x.png" alt="" srcset="" />
                            <li>Location: {location.name}</li>
                            <li>Temperature: {location.main.temp}
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width={20} height={20} color={"#4a4a4a"} fill={"none"}>
    <circle cx="5" cy="6" r="2" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
    <path d="M21 7C20.9557 6.3469 20.8561 5.88667 20.6321 5.5C20.3679 5.04394 19.9878 4.66523 19.5301 4.40192C18.8315 4 17.8936 4 16.0177 4C14.1419 4 13.2039 4 12.5053 4.40192C12.0476 4.66523 11.6676 5.04394 11.4033 5.5C11 6.19615 11 7.13077 11 9V15C11 16.8692 11 17.8038 11.4033 18.5C11.6676 18.9561 12.0476 19.3348 12.5053 19.5981C13.2039 20 14.1419 20 16.0177 20C17.8936 20 18.8315 20 19.5301 19.5981C19.9878 19.3348 20.3679 18.9561 20.6321 18.5C20.8561 18.1133 20.9557 17.6531 21 17" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
</svg></li>
                            <li>Weather: {location.weather[0].description}

                                
                            </li>
                            <li>Humidity: {location.main.humidity}%
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width={20} height={20} color={"#4a4a4a"} fill={"none"}>
    <path d="M21 5.63247C19.8635 4.81397 18.4095 4.81397 17.273 5.63247C16.5877 6.13474 15.6685 6.11614 14.9833 5.61388C13.8468 4.79537 12.3928 4.79537 11.273 5.61388C10.571 6.11614 9.68524 6.11614 9 5.61388" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    <path d="M3 9.37672C4.16839 10.1953 5.66323 10.1953 6.83162 9.37672C7.53608 8.87443 8.46392 8.87443 9.16838 9.37672C10.3368 10.1953 11.8488 10.2139 13 9.39531" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    <path d="M21 14.6233C19.8635 13.8047 18.4095 13.8047 17.273 14.6233C16.5877 15.1256 15.6852 15.1256 15 14.6233C13.8635 13.8047 12.3928 13.7861 11.273 14.6047C10.571 15.107 9.68524 15.107 9 14.6047" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    <path d="M3 18.3767C4.16839 19.1953 5.66323 19.1953 6.83162 18.3767C7.53608 17.8744 8.46392 17.8744 9.16838 18.3767C10.3368 19.1953 11.8488 19.2139 13 18.3953" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
</svg>
                            </li>
                            <li>Wind Speed: {location.wind.speed} m/s
                            <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" width={20} height={20} color={"#4a4a4a"} fill={"none"}>
    <path d="M13.5 13L17 9M14 15C14 16.1046 13.1046 17 12 17C10.8954 17 10 16.1046 10 15C10 13.8954 10.8954 13 12 13C13.1046 13 14 13.8954 14 15Z" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    <path d="M6 12C6 8.68629 8.68629 6 12 6C13.0929 6 14.1175 6.29218 15 6.80269" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" />
    <path d="M2.50006 12.0001C2.50006 7.52172 2.50006 5.28255 3.8913 3.8913C5.28255 2.50006 7.52172 2.50006 12.0001 2.50006C16.4784 2.50006 18.7176 2.50006 20.1088 3.8913C21.5001 5.28255 21.5001 7.52172 21.5001 12.0001C21.5001 16.4784 21.5001 18.7176 20.1088 20.1088C18.7176 21.5001 16.4784 21.5001 12.0001 21.5001C7.52172 21.5001 5.28255 21.5001 3.8913 20.1088C2.50006 18.7176 2.50006 16.4784 2.50006 12.0001Z" stroke="currentColor" strokeWidth="1.5" />
</svg>
                            </li>
                        </ul>
                    </>
                ) : (
                    <h1>Loading...</h1>
                )}
            </div>
        );
    }
