    if(navigator.geolocation.getCurrentPosition)
    {
        navigator.geolocation.getCurrentPosition(function(position) {

            const limit=1;
            const lat = position.coords.latitude;
            const lon = position.coords.longitude;
             const appid = "6a4306a0f2c73c28dc5c3bbf739e87d0";



             useEffect(function(){
                async function fetchMovies(){
                  setIsloading(true);
                const res =await fetch(
               
                  `http://www.omdbapi.com/?apikey=e4c29e17&s=man`
                );//it fetches all the mavies
                const data =await res.json();
                console.log(data);
                setMovies(data.Search);
                setIsloading(false);
            
                }
                fetchMovies();
              
                
              },[]);

             

 fetch(`http://api.openweathermap.org/geo/1.0/reverse?lat=${lat}&lon=${lon}&limit=${limit}&appid=${appid}`)
.then(res => res.json())
.then(data => console.log(data))

            //now include the latitude and longitude and fetch api
            StoreLocation(lat,lon);
            //fething the locatin from the api
        }, 
        function ()
        {
            alert('No location dictatedðŸ˜’')
        }
    )
}
//Save data into localstorage
function StoreLocation(lat,lon,)
{
    const LocaionCurrent= { lat:lat ,lon:lon};
        sessionStorage.setItem('MyLocation',JSON.stringify(LocaionCurrent));
}

  return (
    <div>
