  { code: [56], icon: "üåß" },           // Freezing Drizzle: Light intensity
  { code: [57], icon: "üåßÔ∏è" },          // Freezing Drizzle: Dense intensity
  { code: [61], icon: "üå¶" },           // Rain: Slight intensity
  { code: [63], icon: "üåß" },           // Rain: Moderate intensity
