  { code: [73], icon: "üå®" },           // Snow fall: Moderate intensity
  { code: [75], icon: "üå®Ô∏è" },          // Snow fall: Heavy intensity
  { code: [77], icon: "‚ùÑÔ∏è" },          // Snow grains
  { code: [80], icon: "üåß" },           // Rain showers: Slight intensity
  { code: [81], icon: "üåßÔ∏è" },          // Rain showers: Moderate intensity
  { code: [82], icon: "üåßÔ∏è" },          // Rain showers: Violent intensity
  { code: [85], icon: "üå®" },           // Snow showers: Slight intensity
  { code: [86], icon: "üå®Ô∏è" },          // Snow showers: Heavy intensity
  { code: [95], icon: "üå©" },           // Thunderstorm: Slight or moderate
  { code: [96], icon: "‚õàÔ∏è" },          // Thunderstorm with slight hail
  { code: [99], icon: "‚õàÔ∏è" },          // Thunderstorm with heavy hail
  { code: [21], icon: "üå¨Ô∏è" },          // Breezy conditions
  { code: [22], icon: "üå™Ô∏è" },          // Tornado
  { code: [31], icon: "üå´Ô∏è" },          // Mist
  { code: [32], icon: "üåÅ" },           // Dense fog
  { code: [41], icon: "üå§Ô∏è" },          // Partly sunny
  { code: [42], icon: "‚õÖÔ∏è" },          // Intermittent clouds
  { code: [43], icon: "üå•Ô∏è" },          // Mostly cloudy
  { code: [51, 52], icon: "üåß" },       // Light rain or drizzle
  { code: [54], icon: "üåßÔ∏è" },          // Heavy drizzle
  { code: [62], icon: "üåßÔ∏è" },          // Steady rain
  { code: [83], icon: "üå®Ô∏è" },          // Hail showers
  { code: [84], icon: "üå®" },           // Sleet
];


function getWeatherIcon(wmoCode) {
  // Use the find() method to search through the weatherIcons array
  const match = weatherIcons.find(item => item.code.includes(wmoCode));

  // If a matching WMO code is found, return the corresponding icon
  // If not found, return a default "‚ùì" icon indicating an unknown weather condition
  return match ? match.icon : "‚ùì";
}

// Example usage:
const icon = getWeatherIcon('03'); // This should return the icon "üå•Ô∏è"
console.log(icon); // Logs "üå•Ô∏è"








